import React, { useState, useEffect } from 'react';
import './Login.css';
import Axios from 'axios';

function Login() {

  const [nome, setNome] = useState("");
    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");
    const [reviewsList, setReviewsList] = useState([]);


    useEffect(()=>{
        Axios.get("http://localhost:3001/api/get").then((response)=>{
            setReviewsList(response.data);
        });
    }, [])

    const submitReview = () =>{

      Axios.post("http://localhost:3001/api/insert", {nome:nome, email:email, senha:senha});

              setReviewsList([...reviewsList, {nome:nome, email:email},]);                  
  };
    
    return (
        <div className='Login-conteiner'>
          <section className='login-subscription'>
            <div className='login-input-areas'>
            <form>
            <h1 className='sign-up'> Cadastre-se e ganhe descontos exclusivos!</h1>
                <input className='login-input' name='nome' type='text' placeholder='Nome Completo' onChange={(e) =>{
                        setNome(e.target.value)
                    }}/>
              </form>
              <form>
                <input className='login-input' name='email' type='email' placeholder='Seu Email' onChange={(e) =>{
                        setEmail(e.target.value)
                    }}/>
              </form>
              <form>
              <input className='senha-input' name='senha' type='password' placeholder='Crie sua senha'onChange={(e) =>{
                        setSenha(e.target.value)
                    }}/>
              </form>
                <button className='botao' onClick={submitReview}>Cadastrar</button>
              <form>
              <h1 className='sign-up'> Faça login para acessar o conteúdo!</h1>
                <input className='login-input' name='email' type='email' placeholder='Email'/>
              </form>
              <form>
              <input className='senha-input' name='senha' type='password' placeholder='Senha'/>
              </form>
                <button className='botao'>Login</button>
            </div>
          </section>
        </div>
)
}

export default Login
