import React, { useState, useEffect } from 'react';
import '/Users/Cleilton/react-website-yt-1/client/src/componets/Sugestoes.css';
import Axios from 'axios';


function Sugestoes() {

    const [nome, setNome] = useState("");
    const [review, setReview] = useState("");
    const [reviewsList, setReviewsList] = useState([]);

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/get").then((response)=>{
            setReviewsList(response.data);
        });
    }, [])
    

    const submitReview = () =>{
        Axios.post("http://localhost:3001/api/insert", {nome:nome, review:review}).then(()=>{
            alert('Inserido com sucesso!');
        });                   
    };


    return (
        <div>
            <div className='form'>
                <label>Nome</label>
                    <input name='nome' type='text' onChange={(e) =>{
                        setNome(e.target.value)
                    }}/>
                    <label>Sugestão de cursos</label>
                    <input name='review' type='text' onChange={(e) =>{
                        setReview(e.target.value)
                    }}/>
                    <button onClick={submitReview}>Enviar</button>

                    {reviewsList.map((val) => {
                        return <h1>Nome: {val.nome} | Sugestão de cursos: {val.review}</h1>
                    })}
            </div>
        </div>
    )
}

export default Sugestoes
